mapper.select:
select p.prod_id ,p.title , p.main_pic , p.original_price , p.separate_price,p.cate_id, p.group_price,p.sales,p.racking ,c.cate_name from t_product p left join t_product_category c on p.cate_id = c.cate_id where is_delete = 0
AND p.title like concat('%',#{title},'%')
and p.cate_id = #{cateId}
and p.racking = #{racking}
ORDER by p.prod_id

SELECT count(*) from
    (
        select prod_id from t_product
where
is_delete = 0

    AND title like concat('%',#{title},'%')


    and cate_id = #{cateId}


    and racking = #{racking}

        GROUP BY prod_id
    ) a
